package week1.day2homeassignment;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int number=-10;
       if(number<0)
       {
    	   number=number*(-1);
    	   System.out.println("The given number is :"  + number + "negative");
    	   System.out.println("The given number is converted to positive as : " +number);
       }
       
	}

}
