package week1.day2homeassignment;

public class NumberPositiveOrNegative {


	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=50;
		if (num>0)
		{
		System.out.println("the given number is :" + num + "is positive");
		}
		else if(num<0)
		{
		System.out.println("the given number is :" + num + "is negative");
		}
		else
		{
	
		System.out.println("the given number is equal");
		}
	}

}
